mapper.update:
UPDATE concept
SET deleted = now()
WHERE key = #{key,jdbcType=OTHER}

mapper.select:
SELECT *
FROM concept c
WHERE c.key = #{key,jdbcType=OTHER}

SELECT *
FROM concept c

  JOIN to_tsquery('english',regexp_replace(unaccent(trim(#{query})),'\s+',':*&')||':*') AS query ON 

WHERE c.deleted IS NULL

  AND c.vocabulary_key = #{vocabularyKey}


  AND c.parent_key = #{parentKey}


  AND c.replaced_by_key = #{replacedByKey}


  AND c.name = #{name}

ORDER BY ts_rank_cd(c.fulltext_search, query) DESC, c.created DESC, c.key

  LIMIT #{page.limit} OFFSET #{page.offset}

SELECT COUNT(*)
FROM concept c

  JOIN to_tsquery('english',regexp_replace(unaccent(trim(#{query})),'\s+',':*&')||':*') AS query ON 

WHERE c.deleted IS NULL

  AND c.vocabulary_key = #{vocabularyKey}


  AND c.parent_key = #{parentKey}


  AND c.replaced_by_key = #{replacedByKey}


  AND c.name = #{name}
