mapper.sql:
SELECT DISTINCT ON(key) key, name, #{lang} lang , #{vocabName} vocabName FROM ( (SELECT key, name FROM concept${table_suffix} WHERE vocabulary_key = #{vocabularyKey} AND name ilike #{query}||'%' AND deprecated IS NULL ORDER BY name LIMIT 20 ) UNION ALL (SELECT key, name FROM concept${table_suffix} WHERE vocabulary_key = #{vocabularyKey} AND name ilike '%'||#{query}||'%' AND NOT name ilike #{query}||'%' AND deprecated IS NULL ORDER BY name LIMIT 20 ) UNION ALL (SELECT DISTINCT c.key, c.name FROM concept${table_suffix} c JOIN concept_label${table_suffix} cl ON cl.concept_key = c.key WHERE c.vocabulary_key = #{vocabularyKey} AND cl.value ilike #{query}||'%' AND c.deprecated IS NULL
AND cl.language = #{lang}
LIMIT 20 ) UNION ALL (SELECT DISTINCT c.key, c.name FROM concept${table_suffix} c JOIN concept_label${table_suffix} cl ON cl.concept_key = c.key WHERE c.vocabulary_key = #{vocabularyKey} AND cl.value ilike '%'||#{query}||'%' AND NOT cl.value ilike #{query}||'%' AND c.deprecated IS NULL
AND cl.language = #{lang}
LIMIT 20 ) ) t1 LIMIT 20

SELECT key, language, value, created_by, created, modified_by, modified
