mapper.select:
SELECT 
FROM vocabulary v
WHERE v.key = #{key,jdbcType=INTEGER}

SELECT 
FROM vocabulary v
WHERE v.name = #{name,jdbcType=VARCHAR}

SELECT 
FROM vocabulary v

  JOIN to_tsquery('english',regexp_replace(unaccent(trim(#{query})),'\s+',':*&')||':*') AS query ON query @@ fulltext_search

WHERE v.deleted IS NULL

  AND v.deprecated IS NOT NULL


  AND v.name = #{name}


  AND v.namespace = #{namespace}


  AND v.key = #{key}

ORDER BY ts_rank_cd(v.fulltext_search, query) DESC, v.created DESC, v.key

  LIMIT #{page.limit} OFFSET #{page.offset}

SELECT COUNT(*)
FROM vocabulary v

  JOIN to_tsquery('english',regexp_replace(unaccent(trim(#{query})),'\s+',':*&')||':*') AS query ON query @@ fulltext_search

WHERE v.deleted IS NULL

  AND v.deprecated IS NOT NULL


  AND v.name = #{name}


  AND v.namespace = #{namespace}


  AND v.key = #{key}

SELECT key, name FROM vocabulary WHERE #{values, typeHandler=StringArrayTypeHandler} && normalized_values
AND key != #{vocabularyKey}
LIMIT 3

mapper.update:
UPDATE vocabulary
SET deprecated = now(),
deprecated_by = #{deprecatedBy,jdbcType=VARCHAR}


    ,replaced_by_key=#{replacementKey,jdbcType=INTEGER}


    ,replaced_by_key=null


WHERE key = #{key,jdbcType=INTEGER} AND deleted IS NULL
