mapper.select:
SELECT

FROM vocabulary v
WHERE v.key = #{key,jdbcType=BIGINT}

SELECT

FROM vocabulary v
WHERE v.name = #{name,jdbcType=VARCHAR}

SELECT DISTINCT ON (ts_rank_cd(v.fulltext_search, query), v.created, v.key)
FROM vocabulary v
JOIN to_tsquery('english_nostop',regexp_replace(unaccent(trim(#{params.query})),'\s+',':*&')||':*') AS query ON query @@ fulltext_search
LEFT JOIN LATERAL ( SELECT vr.created FROM vocabulary_release vr WHERE vr.vocabulary_key = v.key ORDER BY vr.created DESC LIMIT 1 ) latestRelease ON true LEFT JOIN concept c ON c.vocabulary_key = v.key
WHERE v.deleted IS NULL
AND v.deprecated IS NOT NULL
AND v.name = #{params.name}
AND v.namespace = #{params.namespace}
AND v.key = #{params.key}
AND (latestRelease IS NULL OR v.modified > latestRelease.created OR c.modified > latestRelease.created)
AND (latestRelease IS NOT NULL AND v.modified <= latestRelease.created AND (c IS NULL OR c.modified <= latestRelease.created))
ORDER BY ts_rank_cd(v.fulltext_search, query) DESC, v.created DESC, v.key
LIMIT #{page.limit} OFFSET #{page.offset}

SELECT COUNT(DISTINCT v.key) FROM vocabulary v
JOIN to_tsquery('english_nostop',regexp_replace(unaccent(trim(#{params.query})),'\s+',':*&')||':*') AS query ON query @@ fulltext_search
LEFT JOIN LATERAL ( SELECT vr.created FROM vocabulary_release vr WHERE vr.vocabulary_key = v.key ORDER BY vr.created desc LIMIT 1 ) latestRelease ON true LEFT JOIN concept c ON c.vocabulary_key = v.key
WHERE v.deleted IS NULL
AND v.deprecated IS NOT NULL
AND v.name = #{params.name}
AND v.namespace = #{params.namespace}
AND v.key = #{params.key}
AND (latestRelease IS NULL OR v.modified > latestRelease.created OR c.modified > latestRelease.created)
AND (latestRelease IS NOT NULL AND v.modified <= latestRelease.created AND (c IS NULL OR c.modified <= latestRelease.created))

SELECT DISTINCT ON(key) key, name FROM ( (SELECT key, name FROM vocabulary WHERE name ilike #{query}||'%' AND deleted IS NULL AND deprecated IS NULL ORDER BY name LIMIT 20 ) UNION ALL (SELECT key, name FROM vocabulary WHERE name ilike '%'||#{query}||'%' AND NOT name ilike #{query}||'%' AND deleted IS NULL AND deprecated IS NULL ORDER BY name LIMIT 20 ) UNION ALL (SELECT DISTINCT v.key, v.name FROM vocabulary v JOIN vocabulary_label vl ON vl.vocabulary_key = v.key WHERE vl.value ilike #{query}||'%' AND v.deleted IS NULL AND v.deprecated IS NULL
AND vl.language = #{lang}
LIMIT 20 ) UNION ALL (SELECT DISTINCT v.key, v.name FROM vocabulary v JOIN vocabulary_label vl ON vl.vocabulary_key = v.key WHERE vl.value ilike '%'||#{query}||'%' AND NOT vl.value ilike #{query}||'%' AND v.deleted IS NULL AND v.deprecated IS NULL
AND vl.language= #{lang}
LIMIT 20 ) ) t1 LIMIT 20

SELECT DISTINCT ON(key) key, name
FROM vocabulary


    key != #{vocabularyKey}

  AND normalize_name(name) = #{name,jdbcType=VARCHAR}

LIMIT 3

mapper.update:
UPDATE vocabulary
SET deprecated = now(),
deprecated_by = #{deprecatedBy,jdbcType=VARCHAR}


    ,replaced_by_key=#{replacementKey,jdbcType=BIGINT}


    ,replaced_by_key=null


WHERE key = #{key,jdbcType=BIGINT} AND deleted IS NULL

UPDATE vocabulary_label
SET value = #{value,jdbcType=VARCHAR}
WHERE key = #{key,jdbcType=BIGINT}

mapper.select:
SELECT key, vocabulary_key, language, value
FROM vocabulary_label
WHERE vocabulary_key = #{entityKey,jdbcType=BIGINT}

SELECT key, vocabulary_key, language, value
FROM vocabulary_label
WHERE key = #{key,jdbcType=BIGINT}
